Description Module를 만들 때 사용합니다.
Module 만들기 import { INaraeCore, Module } from &amp;#39;@naraejs/core&amp;#39; const S_MyModule = Symbol(&amp;#39;MyModule&amp;#39;); @Module() export class MyModule { private _core!: INaraeCore; constructor() { makeToModule(S_MyModule, this) .order(0) .start((core: INaraeCore) =&amp;gt; { // START Handler  this._core = core; }) .stop(() =&amp;gt; { // STOP Handler  }) .build(); } }
